namespace Day6 {
	internal class Program {
		static void Main(string[] args) {
			//Input data
			string data = "";

			//Part1
			FindIndex(4, data);
			//Part2
			FindIndex(14, data);
		}

		/// <summary>
		/// Find the code with the inputted <paramref name="size"/> from the <paramref name="data"/> input.
		/// </summary>
		/// <param name="size">The size of the code as <see cref="int"/>.</param>
		/// <param name="data">The inputted data <see cref="string"/>.</param>
		static void FindIndex(int size, string data) {
			int index = 0;

			for(int i = 0; i < data.Length; i++) {
				//Get the code
				string code = data.Substring(i, size);
				//Check if all characters are unique
				if(code.Distinct().Count() == code.Length) {
					index = i + size;
					break;
				}
			}
			Console.WriteLine(index);
		}
	}
}
