namespace Day6 {
	internal class Program {
		static void Main(string[] args) {
            //Input data
            string data = "";

            int index = 0;

            for(int i = 0; i < data.Length; i++) {
                string code = data.Substring(i, 4);
                if(code.Distinct().Count() == 4) {
                    index = i + 4;
                    break;
                }
            }
            Console.WriteLine(index);
        }
	}
}
